# Input argumentsParam([string]$buildNumber, [string]$workspaceFolder)if( $buildNumber ){	Write-Host "Attempting to update project build version to $buildNumber"	# [assembly: AssemblyFileVersion("1.0.0.0")]	$releaseFolder = "$workspaceFolder\src\AppsMonitorErrorProxy"		$pattern = New-Object System.Text.RegularExpressions.Regex "1\.0\.0\.0"	$year = (Get-Date).Year - 2000	$month = (Get-Date).Month	$day = (Get-Date).Day	$newVersion = "$year.$month.$day.$buildNumber"	$assemblyInfoFileName = "AssemblyInfo.cs"	$inputFileName = "$releaseFolder\Properties\$assemblyInfoFileName"	$outputFileName = "$releaseFolder\Properties\New$assemblyInfoFileName"	$ouputFile = [System.IO.File]::CreateText([System.IO.Path]::Combine((Get-Location).Path, $outputFileName ))		$textReadFromInputFile = [System.IO.File]::ReadAllText($inputFileName)		Write-Host "Attempting to update file content from $inputFileName"		$textReadFromInputFile = $pattern.Replace($textReadFromInputFile, $newVersion)	  	Write-Host "After update... $textReadFromInputFile"	  	$ouputFile.Write($textReadFromInputFile)	$ouputFile.Close() 		Remove-Item $inputFileName -Force	Rename-Item $outputFileName $assemblyInfoFileName		Write-Host "Version updated to $newVersion"}else{	Write-Host "Build number not supplied"}